INSERT INTO `wp_d6yyfwbbnq_options` VALUES("16896", "_transient_insta_gallery_user_items_427984675_", "a:3:{s:10:\"pagination\";a:2:{s:11:\"next_max_id\";s:29:\"2060606884495529061_427984675\";s:8:\"next_url\";s:167:\"https://api.instagram.com/v1/users/427984675/media/recent?access_token=427984675.6e628e6.b4c0789ebfdc4ca897f2d8875198e8da&count=33&max_id=2060606884495529061_427984675\";}s:4:\"data\";a:33:{i:0;a:15:{s:2:\"id\";s:29:\"2072943182710346606_427984675\";s:4:\"user\";a:4:{s:2:\"id\";s:9:\"427984675\";s:9:\"full_name\";s:7:\"CENDECO\";s:15:\"profile_picture\";s:174:\"https://scontent.cdninstagram.com/vp/eaa98b907429209b0fe010b4b1703520/5DB6CD17/t51.2885-19/s150x150/12081142_512579752233456_2111300643_a.jpg?_nc_ht=scontent.cdninstagram.com\";s:8:\"username\";s:7:\"cendeco\";}s:6:\"images\";a:3:{s:9:\"thumbnail\";a:3:{s:5:\"width\";i:150;s:6:\"height\";i:150;s:3:\"url\";s:187:\"https://scontent.cdninstagram.com/vp/9067076d06af8aa0aee00eac40e1328b/5DA00AB2/t51.2885-15/e15/s150x150/61953952_341596916510587_7164912390367870232_n.jpg?_nc_ht=scontent.cdninstagram.com\";}s:14:\"low_resolution\";a:3:{s:5:\"width\";i:320;s:6:\"height\";i:320;s:3:\"url\";s:187:\"https://scontent.cdninstagram.com/vp/6409bb69bcda95f216d8d4f98102b37d/5DAB7C42/t51.2885-15/e15/s320x320/61953952_341596916510587_7164912390367870232_n.jpg?_nc_ht=scontent.cdninstagram.com\";}s:19:\"standard_resolution\";a:3:{s:5:\"width\";i:640;s:6:\"height\";i:640;s:3:\"url\";s:187:\"https://scontent.cdninstagram.com/vp/a2c124be1b9aa64e8f927611c86a0ea6/5DBCF62D/t51.2885-15/e15/s640x640/61953952_341596916510587_7164912390367870232_n.jpg?_nc_ht=scontent.cdninstagram.com\";}}s:12:\"created_time\";s:10:\"1561334105\";s:7:\"caption\";a:4:{s:2:\"id\";s:17:\"18077123125033759\";s:4:\"text\";s:2191:\"7️⃣ estrategias para lidiar con personas negativas\nEstas maniobras te permitirán lidiar mejor con las personas pesimistas que se encuentran a tu alrededor.\n\nEstablece límites: No te sientas presionado a escuchar a alguien negativo. Su energía se colará en tu propia vida y, aun sin que te des cuenta, afectará tu actitud. Quizá no puedas controlar las actitudes negativas de alguien, pero sí puedas decidir si dejarás que te afecten o no.\n\nAléjate de los quejumbrosos: Las personas que se quejan de todo nunca mejorarán su vida. No ofrecen soluciones, sólo señalan los problemas. El peligro: tumbarán tus ideas y te harán partícipe de su compasión.\n\nDeshazte de tus empleados negativos: La cultura empresarial es un componente fundamental de tu marca. Ten en cuenta que un miembro tóxico de tu personal podría afectar todo el ambiente en tu negocio.\n\nElige tus batallas: No te dejes irritar tan fácilmente. En lugar de discutir, trata de ignorar los comentarios negativos. Controla tus emociones y evita que la situación se agrave al alejarte de los conflictos innecesarios. ¡Te respetarán por no tomar el camino fácil!\n\nNo sobreanalices la situación: Algunas veces, las personas negativas pueden comportarse de forma irracional. Perderás tiempo y energía valiosos si tratas de darle un sentido a sus acciones. Haz lo posible por evitar involucrarte emocionalmente en sus asuntos.\n\nDesarrolla un sistema de apoyo: Ten la inteligencia emocional suficiente para reconocer cuando necesitas ayuda. Cuando llegues a sentirte dominado por tus emociones, llama a un amigo o mentor y explícale la situación con calma. Con frecuencia, una persona objetiva puede proporcionarte una perspectiva diferente o un nuevo enfoque.\n\nEncarna una actitud positiva: Tu felicidad y bienestar son demasiado importantes para dejar que la opinión de otro te baje el ánimo o afecte la percepción que tienes de ti mismo. Mantén una actitud positiva y comienza a limitar el tiempo que pasas con personas nocivas. Con algo de suerte, tu actitud positiva repelerá a las personas tóxicas hasta alejarlas por completo de ti 😉 - ¡Feliz domingo! -\n\nContenido de: www.entrepreneur.com\";s:12:\"created_time\";s:10:\"1561334105\";s:4:\"from\";a:4:{s:2:\"id\";s:9:\"427984675\";s:9:\"full_name\";s:7:\"CENDECO\";s:15:\"profile_picture\";s:174:\"https://scontent.cdninstagram.com/vp/eaa98b907429209b0fe010b4b1703520/5DB6CD17/t51.2885-19/s150x150/12081142_512579752233456_2111300643_a.jpg?_nc_ht=scontent.cdninstagram.com\";s:8:\"username\";s:7:\"cendeco\";}}s:14:\"user_has_liked\";b:0;s:5:\"likes\";a:1:{s:5:\"count\";i:52;}s:4:\"tags\";a:0:{}s:6:\"filter\";s:6:\"Normal\";s:8:\"comments\";a:1:{s:5:\"count\";i:0;}s:4:\"type\";s:5:\"image\";s:4:\"link\";s:40:\"https://www.instagram.com/p/BzEktd9BJNu/\";s:8:\"location\";N;s:11:\"attribution\";N;s:14:\"users_in_photo\";a:0:{}}i:1;a:15:{s:2:\"id\";s:29:\"2071840906366379332_427984675\";s:4:\"user\";a:4:{s:2:\"id\";s:9:\"427984675\";s:9:\"full_name\";s:7:\"CENDECO\";s:15:\"profile_picture\";s:174:\"https://scontent.cdninstagram.com/vp/eaa98b907429209b0fe010b4b1703520/5DB6CD17/t51.2885-19/s150x150/12081142_512579752233456_2111300643_a.jpg?_nc_ht=scontent.cdninstagram.com\";s:8:\"username\";s:7:\"cendeco\";}s:6:\"images\";a:3:{s:9:\"thumbnail\";a:3:{s:5:\"width\";i:150;s:6:\"height\";i:150;s:3:\"url\";s:187:\"https://scontent.cdninstagram.com/vp/c547ba93f7acd18e0029754a32613be3/5DC452B5/t51.2885-15/e15/s150x150/65000473_361542907894445_4721688417226444644_n.jpg?_nc_ht=scontent.cdninstagram.com\";}s:14:\"low_resolution\";a:3:{s:5:\"width\";i:320;s:6:\"height\";i:320;s:3:\"url\";s:187:\"https://scontent.cdninstagram.com/vp/72bdad76d7afcf9dd78ecb3ad92eb958/5DBF6B45/t51.2885-15/e15/s320x320/65000473_361542907894445_4721688417226444644_n.jpg?_nc_ht=scontent.cdninstagram.com\";}s:19:\"standard_resolution\";a:3:{s:5:\"width\";i:640;s:6:\"height\";i:640;s:3:\"url\";s:194:\"https://scontent.cdninstagram.com/vp/19ab3fe15b289af4ab5c1fc975fa75f8/5DC381B3/t51.2885-15/sh0.08/e35/s640x640/65000473_361542907894445_4721688417226444644_n.jpg?_nc_ht=scontent.cdninstagram.com\";}}s:12:\"created_time\";s:10:\"1561202704\";s:7:\"caption\";a:4:{s:2:\"id\";s:17:\"18077048167053852\";s:4:\"te