gba(0,0,0,0.15) !important;\n}\n\n.card .et_pb_image_wrap {\n    max-width: 100% !important;\n}\n\n.card.et_pb_blurb h2.et_pb_module_header {\n    color: #132633 !important;\n}\n\n.card.et_pb_blurb p {\n    text-align: justify;\n}\n\n.card .et_pb_button {\n    background: #ef7d29;\n    margin-top: 1rem;\n    border-radius: 13px;\n    font-size: 16px !important;\n}\n\n/* Hero de paginas */\n\n.hero {\n    padding-bottom: 10%;\n    padding-top: 10%;\n}\n\n.hero h1 {\n		display: table;\n    background: rgba(0, 0, 0, .75);\n    width: auto;\n    padding: .5rem 1rem;\n    margin: auto;\n}\n\n\n/****\n\nPagina de post general\n\n****/\n\n\n/* contenedor de post de ancho completo */\n\n#main-content>.container:before {\n    width: 0px;\n}\n\n.single #main-content>.container,\n#left-area {\n    padding: 0 !important;\n    width: 100% !important;\n    max-width: 100% !important;\n    min-width: 100% !important;\n}\n\n.single #main-content {\n    padding-top: 0 !important;\n}\n\n/****\n\nPagina de Search\n\n****/\n\n/* Grid de Busqueda */\n\n.grid-search {\n	width: 100%;\n	margin-bottom: 20%;\n  min-height:50vh;\n	display: flex;\n  justify-content: center;\n  align-items: stretch;\n  flex-wrap: wrap;\n}\n.grid-search > div.entry{\n	width: 100%!important;	\n}\n\n.grid-search > div {\n	width: 27%;\n	margin: 2%;\n}\n.grid-search img {\n    height: 4rem;\n}\n\n.grid-search h2 {\n    font-size: 1rem;\n}\n\n.grid-search p {\n    font-size: .7rem;\n}\n\n/****\n\nPagina de Catalogo\n\n****/\n\n/* grib de facultades */\n\n.grid-facultades .et_pb_code_inner {\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    flex-wrap: wrap;\n}\n\n.grid-facultades .et_pb_code_inner>div {\n    width: 20.875%;\n    margin: 1%;\n		height: 500px;\n\n}\n\n.grid-facultades .et_pb_module img {\n    height: 4rem;\n}\n\n.grid-facultades .et_pb_module h2 {\n    font-size: .8rem;\n}\n\n.grid-facultades .et_pb_module p {\n    font-size: .7rem;\n}\n\n\n/****\n\nPagina de Facultades\n\n****/\n\n\n/* listas de diplomados y cursos */\n\n.child-list h1 {\n    color: #ef7d29 !important;\n    line-height: 1.3em;\n}\n\n.child-list ul {\n    color: #ef7d29 !important;\n    font-size: 18px;\n    line-height: 1.9em;\n    text-align: left;\n    list-style-position: inside;\n    padding-left: 2rem !important;\n    padding-bottom: 1em;\n}\n\n.child-list ul a {\n    text-decoration: underline;\n    color: #6b6b6b !important;\n}\n\n\n/****\n\nPagina de Diplomados y Cursos\n\n****/\n\n\n/* detalles con titulos personalizados */\n\n.details h2,\n.details h3 {\n    color: #ef7d29;\n}\n\n.details h3 span {\n    color: #666666 !important;\n    display: inline;\n}\n\n.details span {\n    display: block;\n    margin-bottom: 30px;\n}\n\n/* Boton personalizado del modulo toggle */\n\n.toggle_custom.et_pb_toggle {\n    border: none;\n    border-bottom: 4px solid #ddd !important;\n    transition: 0.4s\n}\n\n.toggle_custom.et_pb_toggle_open {\n    border-bottom: 4px solid #bbb !important;\n}\n\n.toggle_custom .et_pb_toggle_title:before {\n    font-size: 50px !important;\n    content: \"3\" !important;\n    -webkit-transition: 0.3s;\n    transition: 0.3s;\n}\n\n.toggle_custom.et_pb_toggle_open .et_pb_toggle_title:before {\n    color: #bbb;\n    -ms-transform: rotate(180deg);\n    -webkit-transform: rotate(180deg);\n    transform: rotate(180deg);\n}\n\n\n/*****\n\nPagina de In Company\n\n****/\n\n#seccion_in_company h1 {\n    margin-bottom: 1.5rem;\n}\n\n\n/* Formulario de contacto */\n\n.hustle-modal-success.hustle-modal-success_show {\n    height: auto !important;\n    text-align: center;\n    color: white;\n}\n\n#contact_form form {\n    display: flex;\n    justify-content: space-between;\n    flex-wrap: wrap;\n}\n\n#contact_form form p {\n    width: 48%;\n}\n\n#contact_form form input,\ntextarea {\n    width: 100%;\n    border-radius: 4px;\n    font-size: 1rem;\n    background-color: #FCFCFC;\n    box-sizing: border-box;\n    padding: 12px;\n    border: thin solid #E9E9E9;\n}\n\n/* #contact_form form p:nth-child(5n),\n.contactanos_form form p:nth-child(6n) {\n    width: 100% !important;\n} */\n\n#contact_form form p:nth-child(6n) {\n    width: 100% !important;\n}\n\n\n#contact_form form input[type=\"submit\"] {\n    display: block;\n    border: none;\n    width: 70px;\n    background: #ED8200;\n    color: white;\n    padding: 6px 12px;\n    font-size: 0.9rem;\n    box-sizing: border-box;\n		cursor: pointer;\n}\n\n/****\n\nPagina Contáctanos\n\n****/\n\n\n#contact_form {\n    width: 100%;\n}\n\n/* Campus card */\n\n#campus_card {\n    box-shadow: 0 0 2px 1px rgba(0, 0, 0, 0.15);\n    box-sizing: border-box;\n}\n\n#campus_card h3 {\n    color: #ED8200;\n}\n\n#campus_card div {\n    margin-top: 5px;\n}\n\n#campus_card label {\n    font-weight: bold;\n    font-size: 14px;\n    display: block;\n\n}\n\n#campus_card p {\n    padding: 0;\n}\n\n\n/****\n\nPagina Eventos\n\n****/\n\n/* Grid Eventos */\n\n.grid-event .et_pb_code_inner {\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    flex-wrap: wrap;\n}\n\n.grid-event .et_pb_code_inner>div {\n    width: 24.667%;\n    margin: 2%;\n}\n\n/* Event card */\n\n.event {\n    cursor: pointer;\n    display: flex;\n    flex-direction: column;\n    margin: auto;\n    position: relative;\n    background-color: #fff;\n    transition: box-shadow .25s;\n    border-radius: .2rem;\n    overflow: hidden;\n    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);\n}\n\n.event-image img {\n    display: block;\n    border-radius: .2rem .2rem 0 0;\n    position: relative;\n    left: 0;\n    right: 0;\n    top: 0;\n    bottom: 0;\n    width: 100%;\n    height: 220px;\n}\n\n.event-content {\n    height: 100px;\n    display: flex;\n    align-items: stretch;\n    border-radius: 0 0 .2rem .2rem;\n}\n\n.event-content .event-title {\n    line-height: 1.5rem;\n    padding: .5rem;\n		font-style: italic;\n}\n\n.event-date {\n    background-color: #ef7d29;\n    color: white;\n    padding: .5rem 1rem;\n    display: flex;\n    flex-direction: column;\n    justify-content: center;\n		transition: all .3s ease-out;\n}\n\n.event-date span {\n    line-height: 1rem;\n    display: block;\n    text-align: center;\n}\n\n.event-date .month {\n    font-weight: 700;\n    text-transform: uppercase;\n}\n\n.event:hover .event-date\n{\n	padding: .5rem;\n}\n\n\n/* Event full */\n\n.event-full {\n    overflow: hidden;\n}\n\n.event-full .event-content>p {\n    font-size: 16px !important;\n    text-align: justify;\n}\n\n.event-full .event-image {\n    width: 50%;\n    float: left;\n    position: relative;\n    margin-right: 1rem;\n}\n\n.event-full .event-image .event-date {\n    position: absolute;\n    top: 0;\n    left: 0;\n    height: auto;\n}\n\n.event-full .event-date {\n    padding: 1rem 1.5rem;\n}\n\n.event-full .event-date span {\n    font-size: 1.3rem;\n    line-height: 0.9;\n}\n\n/****\n \n Pagina individual de Eventos\n\n****/\n\narticle.evento.type-evento div.entry-content {\n    margin: 12% 5% 5% 5%;\n}\n\n/****\n \nIn Company Responsive \n\n***/\n\n#in_company_img section {\n    height: 450px;\n}\n\n#in_company_img section .header-content h1 {\n    font-size: 48px;\n}\n\n@media (min-width: 320px) {\n    #in_company_img section {\n        height: 300px;\n    }\n\n    #in_company_img section .header-content h1 {\n        font-size: 25px;\n    }\n}\n\n@media (min-width: 576px) {\n    #in_company_img section {\n        height: 650px;\n    }\n\n    #in_company_img section .header-content h1 {\n        font-size: 48px;\n    }\n}\n\n@media (max-width: 640px) {\n    #seccion_in_company h1 {\n        font-size: 1.4rem !important;\n    }\n}\n\n@media (min-width: 992px) {\n    #in_company_img section {\n        height: 260px;\n    }\n\n    #in_company_img section .header-content h1 {\n        font-size: 38px;\n    }\n}\n\n@media (min-width: 1200px) {\n    #in_company_img section {\n        height: 350px;\n    }\n\n    #in_company_img section .header-content h1 {\n        font-size: 48px;\n    }\n}\n\n/****\n \nCatáctanos Responsive \n\n***/\n@media (max-width: 992px) {\n    #seccion_contactanos .et_pb_column_1 {\n        order: 2;\n    }\n\n    #contact_form form p {\n        width: 100% !important;\n    }\n}\n\n\n/****\n\nGrid Facultades\n\n****/\n\n@media (max-width: 980px) {\n    .grid-facultades .et_pb_code_inner>div {\n        width: 47.25%;\n        margin: 2%;\n				height: 480px\n    }\n}\n\n@media (max-width: 479px) {\n    .grid-facultades .et_pb_code_inner>div {\n        width: 100% !important;\n        margin: 2%;\n    }\n}\n\n/****\n\nGrid Eventos\n\n****/\n@media (max-width: 980px) {\n\n    .grid-event .et_pb_code_inner>div {\n        width: 60%;\n    }\n}\n\n@media (max-width: 479px) {\n\n    .grid-event .et_pb_code_inner>div {\n        width: 80% !important;\n    }\n}\n\n/****\n\nPagina Eventos Responsive\n\n****/\n\n@media (max-width: 425px) {\n\n    .event-full .event-image {\n        width: 100%;\n        margin-right: 0px;\n    }\n\n    .event-full .event-date {\n        padding: .5rem .7rem;\n    }\n\n    .event-full .event-date span {\n        font-size: .7rem;\n        line-height: 0.7;\n    }\n}\n\n#seccion_suscripcion .et_pb_header_content_wrapper {\n    display: flex;\n    flex-wrap: wrap;\n    justify-content: center;\n}\n\n/****\n\nPagina de Busqueda Responsive\n\n****/\n@media (max-width: 980px) {\n.grid-search > div {\n	width: 45%;\n}\n}\n@media (max-width: 479px) {\n.grid-search > div {\n	width: 100% !important;\n}\n}\n\n/****\n\nFotter Global Responsive\n\n****/\n\n@media (max-width: 980px) {\n#menu-social.footer-menu\n{\njustify-content: center!important;\n}\n}\n\n/****\nTitulo con line horizontal Responsive \n****/\n@media (max-width: 980px) {\n.title-line {\n    display: block;\n    white-space: normal!important;\n}\n\n.title-line:after {\n    border-top: 3px solid #eeeff1;\n    content: \'\';\n    display: block;\n    position: relative;\n    top: 0.5em;\n    width: 100%;\n    left: 1.5%;\n		padding-bottom: 1.5rem;\n}\n}\n/****\n\nEn el campus\n\n****/\n\n\n/* Slider */\n\n#seccion_campus .et_pb_container .clearfix .et_pb_empty_slide {\n	height: auto;\n}\n\n#seccion_campus .et_pb_slide {\n    padding: 0;\n}\n\n#seccion_campus .et_pb_slide_image {\n	margin: 0 !important;\n}\n\n#seccion_campus .et_pb_slide_image img {\n	max-height: 100% !important;\n	width: 100%;\n}\n\n#seccion_campus .et_pb_slide_description {\n	padding: 0;\n}\n#seccion_campus .et_pb_slide_image {\n	width: 100% !important;\n	position: initial !important;\n}", "Cendeco", "", "inherit", "closed", "closed", "", "11-revision-v1", "", "", "2019-05-30 14:55:52", "2019-05-30 14:55:52", "", "11", "http://cendeco.flywheelsites.com/2019/05/30/11-revision-v1/", "0", "revision", "", "0");
