INSERT INTO `wp_d6yyfwbbnq_posts` VALUES("1339", "1", "2019-05-30 14:45:42", "2019-05-30 14:45:42", "/****\n\nEstilos Generales\n\n****/\n\n\n/* Menu principal */\n\n\nheader#main-header,\n.et-fixed-header#main-header {\n    box-shadow: 0px 2px 6px 0px rgba(0, 0, 0, 0.1) !important;\n}\n\n#top-menu li {\n    padding: 0px 1rem !important;\n}\n\nul:not(.footer-menu) .current_page_item {\n    border-bottom: .25rem solid #ef7c29;\n}\n\n/* Menu segundario */\n\n#menu-social.footer-menu {\n    justify-content: flex-end !important;\n}\n\n#menu-social.footer-menu li {\n    font-size: 1.2rem !important;\n}\n\n/* Footer global */\n\n#main-footer {\n    display: none;\n}\n\n.footer-menu {\n    width: 100% !important;\n    margin: auto !important;\n    padding: 0px !important;\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    height: 15vh;\n}\n\n.footer-menu a {\n    padding: 0px !important;\n    color: #ffffff !important;\n}\n\n/* Slider principal */\n\n.main-full-slider .et_pb_slides > div \n{\nmin-height: 90vh;\n}\n\n/* botones */\n\n.button-custom {\n	  cursor: pointer;\n    border-width: 0px !important;\n    border-radius: 13px;\n    font-size: 16px;\n    background-color: #ef7d29;\n    color: white;\n    padding: .5rem 2rem !important;\n}\n\n/* Titulo con linea horizontal */\n\n.title-line {\n    display: table;\n    white-space: nowrap;\n}\n\n.title-line:after {\n    border-top: 3px solid #eeeff1;\n    content: \'\';\n    display: table-cell;\n    position: relative;\n    top: 0.5em;\n    width: 100%;\n    left: 1.5%;\n}\n\n/* Listas con puntos personalizados */\n\n.et_pb_row ul:not(.nav) {\n    list-style: none !important;\n}\n\n.et_pb_row ul:not(.nav) li::before {\n    content: \"\\2022\";\n    color: #ef7d29;\n    font-weight: bold;\n    display: inline-block;\n    width: 1em;\n    margin-left: -1em;\n}\n\n/* Cards en toda la aplicacion */\n\n.card {\n    height: fit-content;\n}\n\n.card.et_pb_blurb {\n    box-shadow: 0px 12px 18px 1px r